import "./FormLayout.scss";

import {Col, Container, Row, Visible} from "react-grid-system";
import {ImageConfig} from "../../constants";
import React, {useCallback} from "react";
import StatusCardComponent from "../../shared/components/status-card/statusCardComponent";
import {LeftArrow} from "../../constants/ImageConfig";

export interface FormLayoutComponentProps {
    posterImagePath: any;
    formMainTitle?: string;
    toolTipInfo?: any;
    onBackNavigation?: () => void;
}

const FormLayoutComponent = (props: React.PropsWithChildren<FormLayoutComponentProps>) => {

    const {posterImagePath, onBackNavigation, formMainTitle, toolTipInfo, children} = props;

    const handleBackNavClick = useCallback(() => {
        if (onBackNavigation) {
            onBackNavigation();
        }
    }, [onBackNavigation]);

    const renderFormBranding = useCallback(() => {
        return <div className="form-branding">
            <div className="form-back-navigation-wrapper">
                {onBackNavigation && <div className="form-back-navigation" onClick={handleBackNavClick}>
                    <LeftArrow className="form-back-navigation-icon" alt="nav-back-icon"/>
                    <span className="form-back-navigation-label">Back</span>
                </div>}
            </div>
            <div className="form-branding-logo">
                <img src={ImageConfig.sdiLogo} alt="sdilogo"/>
            </div>
        </div>
    }, [onBackNavigation, handleBackNavClick]);

    return (
        <div className="form-layout-component">
            <Visible xs sm md>
                {renderFormBranding()}
            </Visible>
            <Row>
                <Col sm={12} md={12} lg={6} className="form-poster-meta-wrapper">
                    {posterImagePath && <div className="form-poster-image">
                        <img src={posterImagePath} alt={posterImagePath}/>
                    </div>}
                    {formMainTitle && <div className="form-main-title">
                        {formMainTitle}
                    </div>}
                    {toolTipInfo && <div className="form-tooltip-info">
                        <StatusCardComponent type={"default"} wrap={true}>
                            <div dangerouslySetInnerHTML={{__html: toolTipInfo}}/>
                        </StatusCardComponent>
                    </div>}
                </Col>
                <Col sm={12} md={12} lg={6} className={'form-wrapper'}>
                    <Visible lg xl xxl xxxl>
                        {renderFormBranding()}
                    </Visible>
                    <div className={"form-body"}>
                        <Container>
                            <Row>
                                <Col sm={1}></Col>
                                <Col sm={10}>
                                    {children}
                                </Col>
                                <Col sm={1}></Col>
                            </Row>
                        </Container>
                    </div>
                </Col>
            </Row>
        </div>
    );

};

export default FormLayoutComponent;


